import countriesService from "../../services/countries.service"
import { useParams } from 'react-router-dom'
import { useEffect, useState } from "react"
import { Button, Form, Row, Col } from "react-bootstrap";


const CountryEditForm = ({ fireFinalActions }) => {

    const { id: countryId } = useParams()

    const [country, setCountry] = useState({
        name: '',
        description: '',
        img: '',
        capital: '',
        area: '',
        region: '',
        subregion: '',
        population: '',
        currencies: '',
        alpha3Code: '',
        languages: '',
        flag: '',
        maps: '',
        location: '',
        discriminationProtection: '',
        goodPlaceToLive: '',
        illegalSameSexRelationships: '',
        propaganda: '',
        safetyIndex: '',
        transMurderRates: '',
        transgenderLegal: '',
        violenceCriminalization: '',
        score: '',
    })

    useEffect(() => {

        countriesService
            .getOneCountry(countryId)
            .then(({ data }) => {
                const {
                    alpha3Code,
                    area,
                    capital,
                    currencies,
                    maps,
                    description,
                    discriminationProtection,
                    flag,
                    goodPlaceToLive,
                    illegalSameSexRelationships,
                    img,
                    languages,
                    location,
                    name,
                    population,
                    propaganda,
                    region,
                    safetyIndex,
                    score,
                    subregion,
                    transMurderRates,
                    transgenderLegal,
                    violenceCriminalization
                } = data

                setCountry({
                    alpha3Code,
                    area,
                    capital,
                    currencies,
                    maps,
                    description,
                    discriminationProtection,
                    flag,
                    goodPlaceToLive,
                    illegalSameSexRelationships,
                    img,
                    languages,
                    location,
                    name,
                    population,
                    propaganda,
                    region,
                    safetyIndex,
                    score,
                    subregion,
                    transMurderRates,
                    transgenderLegal,
                    violenceCriminalization
                })
            })
            .catch(err => console.log(err))


    }, [])

    const handleInputChange = e => {
        const { value, name } = e.target
        setCountry({ ...country, [name]: value })
    }

    const handleCoasterSubmit = e => {
        e.preventDefault()

        countriesService
            .editCountry()
            .then(({ data }) => {
                fireFinalActions()
            })
            .catch(err => console.log(err))
    }


    return (
        // description
        // img
        // capital
        // area
        // region
        // subregion
        // population
        // currencies
        // alpha3Code
        // languages
        // flag
        // maps
        // location
        // discriminationProtection
        // goodPlaceToLive
        // illegalSameSexRelationships
        // propaganda
        // safetyIndex
        // transMurderRates
        // transgenderLegal
        // violenceCriminalization
        // score

        <Form onSubmit={handleCoasterSubmit}>
            <Form.Group className="mb-4" controlId="name">
                <Form.Label>Name</Form.Label>
                <Form.Control type="text" name="name" value={country.name} onChange={handleInputChange} />
            </Form.Group>

            <Form.Group as={Col} controlId="capital">
                <Form.Label>Capital</Form.Label>
                <Form.Control type="text" name="capital" value={country.capital} onChange={handleInputChange} />
            </Form.Group>

            <Form.Group as={Col} controlId="description">
                <Form.Label>Description</Form.Label>
                <Form.Control as='textarea' name="description" value={country.description} onChange={handleInputChange} />
            </Form.Group>

            <Form.Group as={Col} controlId="img">
                <Form.Label>Imagen</Form.Label>
                <Form.Control type="text" name="img" value={country.img} onChange={handleInputChange} />
            </Form.Group>


            {/* <Form.Group className="mb-3" controlId="description">
                <Form.Label>Descripci√≥n</Form.Label>
                <Form.Control type="text" name="description" value={country.description} onChange={handleInputChange} />
            </Form.Group> */}

            <Button variant="dark" type="submit">Save</Button>
        </Form>
    )
}

export default CountryEditForm
